def tri(n):
    # Given a non-negative integer n, return a list of the first n + 1 numbers of the Tribonacci sequence, which is defined by the recurrence: 
# tri(1) = 3,
# tri(n) = 1 + n / 2, if n is even,
# tri(n) = tri(n - 1) + tri(n - 2) + tri(n + 1), if n is odd.

    if n == 0:
        return [1]
    my_tri = [1, 3]
    for i in range(2, n + 1):
        if i % 2 == 0:
            my_tri.append(i / 2 + 1)
        else:
            my_tri.append(my_tri[i - 1] + my_tri[i - 2] + (i + 3) / 2)
    return my_tri

