def fibfib(n: int):
    # Define a function to efficiently compute the n-th element of the FibFib number sequence, where:
# fibfib(0) == 0,
# fibfib(1) == 0,
# fibfib(n) == fibfib(n-1) + fibfib(n-2) + fibfib(n-3) for n >= 2.

    if n == 0:
        return 0
    if n == 1:
        return 0
    if n == 2:
        return 1
    return fibfib(n - 1) + fibfib(n - 2) + fibfib(n - 3)

def circular_shift(x, shift):
    # Circularly shift the digits of the integer x to the right by the specified shift amount and return the result as a string. If the shift is greater than the number of digits, return the digits reversed.

    s = str(x)
    if shift > len(s):
        return s[::-1]
    else:
        return s[len(s) - shift:] + s[:len(s) - shift]

