def search(lst):
    # Given a non-empty list of positive integers, return the greatest integer that is greater than zero and has a frequency greater than or equal to its own value. If no such value exists, return -1.

    frq = [0] * (max(lst) + 1)
    for i in lst:
        frq[i] += 1;

    ans = -1
