def encrypt(s):
    # Create a function `encrypt` that takes a string as an argument and 
# returns a string encrypted with the alphabet rotated by four places.

    d = 'abcdefghijklmnopqrstuvwxyz'
    out = ''
    for c in s:
        if c in d:
            out += d[(d.index(c)+2*2) % 26]
        else:
            out += c
    return out

